import {connect} from 'react-redux'
import {logout} from "../../redux/actions/login_action"
import React from 'react'
import axios from "axios"
import { useNavigate } from 'react-router-dom'
import { Col, Row, Button, Divider, Empty, List, Card, Input, Image} from 'antd';
import useInterval from '../../utils/timer'

import './index.scss'

const { TextArea } = Input

const gridStyle = {
    width: '33%',
    textAlign: 'center',
}

const init_timer = 3000
const gap_timer = 1000

function EditUI (props) {
    const navigate = useNavigate()

    const [descriptionList, setDescriptionList] = React.useState([])
    const [loadings, setLoadings] = React.useState(false)
    const [clickedIndex , setClickedIndex] = React.useState(-1)
    const [imgList, setImgList] = React.useState([
        {'image': 'error'},
        {'image': 'error'},
        {'image': 'error'}
    ])

    const [reviseDescription, setReviseDescription] = React.useState(null)

    const [img_id, changeImgId] = React.useState(2)
    const [timer, setTimer] = React.useState(init_timer)

    useInterval(() => {
        if (img_id >= 2) {
            if (imgList[0].image !== 'error') {
                changeImgId(0)
                setTimer(gap_timer)
            } else if (imgList[1].image !== 'error') {
                changeImgId(1)
                setTimer(gap_timer)
            }
        } else {
            if (img_id === 1) {
                setTimer(init_timer)
            }
            changeImgId(img_id + 1)
        }
    }, timer)

    React.useEffect(()=>{
        axios.get(
            `/backend/edit/get_list/user_id=${props.state.user_id}`,
        ).then(
            function(response) {
                setDescriptionList(response.data.descriptions)
            },
            function(error) {
                console.log('失败', error)
            }
        )
    },[]) //仅在挂载和卸载的时候执行

    const toEdit = (index, e) => {
        setClickedIndex(descriptionList[index])
        setReviseDescription(descriptionList[index])
        axios.post(
            `/backend/edit/get_instance`,
            {
                user_id: props.state.user_id,
                password: props.state.password,
                description_id: descriptionList[index].id,
            }
        ).then(
            function(response) {
                if (response.data.result === true) {
                    // console.log('成功', response.data)
                    setImgList(response.data.image)
                } else {
                    alert(response.data.information)
                }
            },
            function(error) {
                console.log('失败', error)
            }
        )
    }

    const toHome = () => {
        navigate('/home', {
            replace: false,
            state: {},
        })
    }

    const descriptionChange = (e) => {
        // console.log(e.target.value)
        setReviseDescription({
            id: reviseDescription.id,
            sentence: e.target.value,
            action: reviseDescription.action,
        })
    }

    const actionChange = (e) => {
        // console.log(e.target.value)
        setReviseDescription({
            id: reviseDescription.id,
            sentence: reviseDescription.sentence,
            action:  e.target.value,
        })
    }

    const toSubmit = () => {
        setLoadings(true)
        axios.post(
            `/backend/edit/submit_data`,
            {
                user_id: props.state.user_id,
                password: props.state.password,
                description_id: reviseDescription.id,
                sentence: reviseDescription.sentence,
                action: reviseDescription.action,
            }
        ).then(
            function(response) {
                if (response.data.result === true) {
                    // console.log('成功', response.data)
                    alert(response.data.information)
                    setLoadings(false)
                    window.location.reload()
                } else {
                    alert(response.data.information)
                    setLoadings(false)
                }
            },
            function(error) {
                setLoadings(false)
            }
        )
    }

    return (
        <div className='edit'>
            <div>
                <Row>
                    <Col span={6}>
                        <Button type="text" disabled>
                        欢迎 {props.state.name}
                        </Button>
                    </Col>
                    <Col span={12}>
                        <Button type="text" disabled>
                        标注量: {props.state.annotations}
                        </Button>
                    </Col>
                    <Col span={6}>
                        <Button type="primary" danger onClick={toHome}>回到主页</Button>
                    </Col>
                </Row>

                <Divider orientation="left" plain>
                待修改内容
                </Divider>
            </div>

            <div>
                {clickedIndex < 0 ? <></> : (
                    <>
                    <Card title="连续三帧图片">
                        {/* <Card.Grid hoverable={false} style={gridStyle}>
                            <Image
                                width={240}
                                height={240}
                                src={imgList[0].image}
                                fallback="data:image/png;base64,"
                            />
                        </Card.Grid>
                        <Card.Grid hoverable={false} style={gridStyle}>
                            <Image
                                width={240}
                                height={240}
                                src={imgList[1].image}
                                fallback="data:image/png;base64,"
                            />
                        </Card.Grid> */}
                        {/* <Card.Grid hoverable={false} style={gridStyle}> */}
                            <Image
                                width={768}
                                height={480}
                                src={imgList[img_id].image}
                                fallback="data:image/png;base64,"
                            />
                        {/* </Card.Grid> */}
                    </Card>

                    <Card
                        style={{
                        width: 900,
                        }}
                    >
                        描述: <TextArea onChange={descriptionChange} rows={4} maxLength={1024} value={reviseDescription.sentence}/>
                        动作: <Input onChange={actionChange} allowClear value={reviseDescription.action}/>
                    </Card>
                    
                    <Button type="primary" onClick={toSubmit} loading={loadings}>提交</Button>

                    <Divider />
                    </>
                )}
            </div>

            <div>
                {descriptionList.length === 0 ? <Empty /> : (
                    <div>
                        <List
                            itemLayout="horizontal"
                            dataSource={descriptionList}
                            renderItem={(item, index) => (
                            <List.Item onClick={toEdit.bind(this, index)}>
                                <List.Item.Meta
                                title={<p>DescriptionId: {item.id}</p>}
                                description={item.sentence}
                                />
                            </List.Item>
                            )}
                        />
                    </div>
                )}
            </div>
            
        </div>
    )
}

export default connect(
    (state) => ({state: state.user}), 
    {
      logout: logout,
    }
  )(EditUI)